{% extends 'base.html.twig' %}

{% block title %}Confirm User Deletion{% endblock %}

{% block body %}
    <div class=\"container mt-5\">
        <div class=\"card\">
            <div class=\"card-body\">
                <h2 class=\"card-title\">Confirm Deletion</h2>
                <p class=\"card-text\">
                    Are you sure you want to delete user <strong>{{ user.name }}</strong> ({{ user.email }})?
                </p>

                <form method=\"post\" action=\"{{ path('admin_user_perform_delete', {'id': user.id}) }}\">
                    <input type=\"hidden\" name=\"_token\" value=\"{{ csrf_token('delete' ~ user.id) }}\">

                    <button type=\"submit\" class=\"btn btn-danger\">
                        <i class=\"fas fa-trash\"></i> Confirm Delete
                    </button>
                    <a href=\"{{ path('admin_user_delete') }}\" class=\"btn btn-secondary\">
                        <i class=\"fas fa-times\"></i> Cancel
                    </a>
                </form>
            </div>
        </div>
    </div>
{% endblock %}", "profile/confirm_delete.html.twig", "C:\\Users\\sbiss\\OneDrive - ESPRIT\\Documents\\urbanlink_Sy\\templates\\profile\\confirm_delete.html.twig");
